import React, { useState } from 'react';

const SignUp = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const [displayName, setDisplayName] = useState('');

    const signupEmailPasswordHandler = (event, email, password) => {
        event.preventDefault();
        setEmail('');
        setPassword('');
        setDisplayName('');
    };

    const onChangeHandler = (event) => {
        const { name, value } = event.currentTarget;

        if (name === 'userEmail') {
            setEmail(value);
        }
        else if (name === 'userPassword') {
            setPassword(value);
        }
        else if (name === 'displayName') {
            setDisplayName(value);
        }
    }

    return (
        <form className="container mt-6">
            {error !== null &&
                <div className="alert alert-warning" role="alert">{error}</div>
            }

            <div className="modal-body">
                <div className="container">

                <div className="form-group">
                        <label for="displayName">Your Name</label>
                        <input type="text"
                            name="displayName"
                            value={displayName}
                            onChange={(event) => onChangeHandler(event)}

                            className="form-control"
                            id="displayName"
                            aria-describedby="displayName"
                        />
                        <small id="emailHelp" className="form-text text-muted">Input your name</small>
                    </div>

                    <div className="form-group">
                        <label for="userEmail">Email address</label>
                        <input type="email"
                            name="userEmail"
                            value={email}
                            onChange={(event) => onChangeHandler(event)}

                            className="form-control"
                            id="userEmail"
                            aria-describedby="userEmail"
                        />
                        <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
                    </div>

                    <div className="form-group">
                        <label for="userPassword">Password</label>
                        <input type="password"
                            name="userPassword"
                            value={password}
                            onChange={(event) => onChangeHandler(event)}
                            className="form-control"
                            id="userPassword"
                        />
                    </div>
                    <div className="form-group form-check">
                        <input type="checkbox" className="form-check-input" id="RememberMe" />
                        <label className="form-check-label" for="RememberMe">Remember Me</label>
                    </div>
                </div>
            </div>
            <div className="modal-footer">
                <button type="button" className="btn" data-dismiss="modal"></button>
                <button type="button"
                    onClick={(event) => signupEmailPasswordHandler(event, email, password)}
                    className="btn btn-primary">
                    Register
                </button>
            </div>
        </form>
    )
}

export default SignUp;
