import React, { Component } from 'react';
import Firebase from '../firebase';

export default class Login extends Component {
    constructor(props) {
        super(props)

        this.signupButton = this.signupButton.bind(this);
        this.signinButton = this.signupButton.bind(this);

        this.state = {
            email: " ",
            password: " "
        }
    }

    signinButton(event) {
        event.preventDefault();

        Firebase.auth().signInWithEmailAndPassword(this.state.email, this.state.password).then((user) => {
            console.log(user);
        }).catch((error) => {
            console.log(error);
        })

    }

    signupButton(event) {
        event.preventDefault();

        Firebase.auth().createUserWithEmailAndPassword(this.state.email, this.state.password).then((user) => {
            console.log(user);
        }).catch((error) => {
            console.log(error);
        })

    }

    handleChange(e){
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    render() {
        return (
            <form>

                <div className="modal-body">
                    <div className="container">

                        <div className="form-group">
                            <label for="userEmail">Email address</label>
                            <input type="email"
                                id="email"
                                name="email"
                                value={this.state.email}
                                onChange={this.handleChange}

                                className="form-control"
                            />
                            <small className="form-text text-muted">We'll never share your email with anyone else.</small>
                        </div>

                        <div className="form-group">
                            <label for="userPassword">Password</label>
                            <input type="password"
                                name="paassword"
                                id="password"
                                value={this.state.password}
                                onChange={this.handleChange}

                                className="form-control"
                            />
                        </div>
                        <div className="form-group form-check">
                            <input type="checkbox" className="form-check-input" id="RememberMe" />
                            <label className="form-check-label" for="RememberMe">Remember Me</label>
                        </div>
                    </div>
                </div>
                <div className="modal-footer">
                    <button type="button" onClick={this.signupButton} className="btn" data-dismiss="modal">SignUp</button>
                    <button type="button" onClick={this.signinButton} className="btn btn-primary">Login</button>
                </div>
            </form>
        )
    }
}
